mapper.select:
SELECT p.id, p.name, p.brand_id, p.category1_id, p.category2_id, p.category3_id, p.unit_name, p.slider_urls, p.spec_value, p.status, p.audit_status, p.audit_message, p.create_time, p.update_time, p.is_deleted, b.name brandName, c1.name category1Name, c2.name category2Name, c3.name category3Name
FROM product as p INNER JOIN brand as b ON b.id = p.brand_id INNER JOIN category as c1 ON c1.id = p.category1_id INNER JOIN category as c2 ON c2.id = p.category2_id INNER JOIN category as c3 ON c3.id = p.category3_id
and p.brand_id = #{productDto.brandId}
and p.category1_id = #{productDto.category1Id}
and p.category2_id = #{category2Id}
and p.category3_id = #{productDto.category3Id}
and p.is_deleted = 0
order by id desc
